import { map } from 'rxjs/operators';
import { Status, statusColor } from './../../models/character.model';
import { combineLatest, Observable } from 'rxjs';
import { CharactersFacadeService } from './../../+state/character-facade.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Character, StatusType } from '../../models/character.model';
import { filter, startWith, switchMap, tap } from 'rxjs/operators';

@Component({
  selector: 'bc-character',
  templateUrl: './character.component.html',
  styleUrls: ['./character.component.scss']
})
export class CharacterComponent implements OnInit {

  character$!: Observable<Character>;
  characterQuote$!: Observable<string>;
  isLoading$!: Observable<boolean>;

  constructor(private router: ActivatedRoute, private charactersFacadeService: CharactersFacadeService) { }

  ngOnInit(): void {
    this.character$ = this.router.params.pipe(
      switchMap(({id}) =>
        this.charactersFacadeService.getCharacter(id).pipe(
          tap(character => {
            if (!character?.char_id) {
              this.charactersFacadeService.loadCharacterById(id);
            }
          })
        )
      )
    );

    this.characterQuote$ = this.character$.pipe(
      filter(character => !!character?.name),
      switchMap(character => this.charactersFacadeService.getCharacterQuote(character.name))
    );
  }
}
<div class="character">
  <button
    class="mb-3"
    [routerLink]="['/']"
    mat-raised-button>
    <mat-icon aria-hidden="false" aria-label="come back icon">arrow_back</mat-icon>
    {{'returnToCharacters' | translate}}
  </button>

  <ng-container *ngIf="character$ | async as character">
    <div class="character-grid" *ngIf="character?.char_id; else loadingCharacter">
      <img loading="lazy"
        class="character-grid__image"
        [src]="character.img"
        alt="{{ character.name + '' + ( 'character.image' | translate )}}"/>
      <ng-container *ngIf="characterQuote$ | async as quote">
        <bc-character-info
          [character]="character"
          [quote]="quote">
        </bc-character-info>
      </ng-container>
    </div>
  </ng-container>
</div>

<ng-template #loadingCharacter>
  <div class="character-grid">
    <div class="character__image__placeholder"></div>
    <div class="character__info__placeholder">
      <div class="character__info__placeholder__line mb-3"></div>
      <div class="character__info__placeholder__line mb-3"></div>
      <div class="character__info__placeholder__line mb-3"></div>
      <div class="character__info__placeholder__line mb-3"></div>
      <div class="character__info__placeholder__line character__info__placeholder__line--bigger"></div>
    </div>
  </div>
</ng-template>
