import { Status, statusColor } from './../../models/character.model';
import { Observable } from 'rxjs';
import { CharactersFacadeService } from './../../+state/character-facade.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Character, StatusType } from '../../models/character.model';
import { switchMap, tap } from 'rxjs/operators';

@Component({
  selector: 'bc-character',
  templateUrl: './character.component.html',
  styleUrls: ['./character.component.scss']
})
export class CharacterComponent implements OnInit {

  character$!: Observable<Character>;

  constructor(private router: ActivatedRoute, private charactersFacadeService: CharactersFacadeService) { }

  ngOnInit(): void {
    this.character$ =  this.router.params.pipe(
      switchMap(({id}) =>
        this.charactersFacadeService.getCharacter(id).pipe(
          tap(character => {
            if (!character?.char_id) {
              this.charactersFacadeService.loadCharacterById(id);
            }
          })
        )
      ),
    );
  }

  public characterStatusChipColor(state: StatusType) {
    console.log(statusColor.get(state));
    return statusColor.get(state);
  }

}
<div class="character">
  <button
    [routerLink]="['/']"
    mat-raised-button>
    <mat-icon aria-hidden="false" aria-label="come back icon">arrow_back</mat-icon>
    {{'returnToCharacters' | translate}}
  </button>

  <div class="character-grid" *ngIf="character$ | async as character">
    <img  class="character-grid__image" [src]="character.img" alt="">
    <div class="character-grid__info">
      <div class="character-grid__info__block mb-3">
        <span>{{ ('character.name' | translate) + ': ' + character.name }}</span>
        <span>{{ ('character.nickname' | translate) + ': ' + character.nickname }}</span>
      </div>

      <div class="character-grid__info__block mb-3">
        <span>{{ ('character.birthday' | translate) + ': ' + character.birthday }}</span>
      </div>

      <div class="character-grid__info__block mb-3">
        <span>{{ ('character.occupation' | translate) + ': ' }}</span>
        <mat-chip-list>
          <mat-chip *ngFor="let ocupation of character.occupation ">{{ ocupation }}</mat-chip>
        </mat-chip-list>
      </div>
      <div class="character-grid__info__block mb-3">
        <span>
          {{ ('character.status' | translate) + ': ' }}
          <mat-chip
            selected
            [color]="characterStatusChipColor(character.status)">
            {{ character.status }}
          </mat-chip>
        </span>
      </div>

    </div>
  </div>
</div>
