import { Status, statusColor } from './../../models/character.model';
import { Observable } from 'rxjs';
import { CharactersFacadeService } from './../../+state/character-facade.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Character, StatusType } from '../../models/character.model';
import { filter, switchMap, tap } from 'rxjs/operators';

@Component({
  selector: 'bc-character',
  templateUrl: './character.component.html',
  styleUrls: ['./character.component.scss']
})
export class CharacterComponent implements OnInit {

  character$!: Observable<Character>;
  characterQuote$!: Observable<string>;

  constructor(private router: ActivatedRoute, private charactersFacadeService: CharactersFacadeService) { }

  ngOnInit(): void {
    this.character$ =  this.router.params.pipe(
      switchMap(({id}) =>
        this.charactersFacadeService.getCharacter(id).pipe(
          tap(character => {
            if (!character?.char_id) {
              this.charactersFacadeService.loadCharacterById(id);
            }
          })
        )
      ),
    );


    this.characterQuote$ = this.character$.pipe(
      filter(character => !!character?.name),
      switchMap(character => this.charactersFacadeService.getCharacterQuote(character.name))
    );
  }

  public characterStatusChipColor(state: StatusType) {
    console.log(statusColor.get(state));
    return statusColor.get(state);
  }

}
<div class="character">
  <button
    class="mb-3"
    [routerLink]="['/']"
    mat-raised-button>
    <mat-icon aria-hidden="false" aria-label="come back icon">arrow_back</mat-icon>
    {{'returnToCharacters' | translate}}
  </button>

  <div class="character-grid" *ngIf="character$ | async as character">
    <img  class="character-grid__image" [src]="character.img" alt="">
    <div class="character-grid__info">
      <div class="character-grid__info__block mb-3">
        <div>
          <span class="character-grid__info__block__label" translate>character.name</span>
          <span class="mr-1">:</span>
          <p class="character-grid__info__block__data">{{character.name}}</p>
        </div>
        <div class="ml-3">
          <span class="character-grid__info__block__label" translate>character.nickname</span>
          <span class="mr-1">:</span>
          <p class="character-grid__info__block__data">{{character.nickname}}</p>
        </div>
      </div>

      <div class="character-grid__info__block mb-3">
        <div>
          <span class="character-grid__info__block__label" translate>character.birthday</span>
          <span class="mr-1">:</span>
          <p class="character-grid__info__block__date">{{character.birthday}}</p>
        </div>
      </div>

      <div class="character-grid__info__block mb-3">
        <div>
          <span class="character-grid__info__block__label" translate> character.occupation </span>
          <span class="mr-1">:</span>
          <mat-chip-list>
            <mat-chip *ngFor="let ocupation of character.occupation ">{{ ocupation }}</mat-chip>
          </mat-chip-list>
        </div>
      </div>

      <div class="character-grid__info__block mb-3">
        <div>
          <span class="character-grid__info__block__label" translate> character.status </span>
          <span class="mr-1">:</span>
          <mat-chip
            selected
            [color]="characterStatusChipColor(character.status)">
            {{ character.status }}
          </mat-chip>
        </div>
      </div>

      <div class="quote__block">
        <p class="character-grid__info__block__quote">
          <mat-icon class="quote quote--rotated">format_quote</mat-icon>
          {{characterQuote$ | async}}

          <mat-icon class="quote">format_quote</mat-icon>
        </p>
      </div>


    </div>
  </div>
</div>


<ng-template #noQuote>
  no tiene frases célebres
</ng-template>
