import { CharactersService } from './../characters.service';
import { Component, OnInit } from '@angular/core';
import { trigger, transition, query, style, stagger, animate } from '@angular/animations';

@Component({
  selector: 'bc-character-list',
  templateUrl: './character-list.component.html',
  styleUrls: ['./character-list.component.scss'],
  animations: [
    trigger('slideInRightList', [
        transition(':enter, :leave', [
          query(':enter', [
              style({ transform: 'translateX(100%)', opacity: 0 }),
              stagger('300ms', [
                animate('300ms ease-in', style({ transform: 'translateX(0)', opacity: 1 })),
              ]),
            ],
            { optional: true }
          )
        ])
      ]
    )
  ]
})
export class CharacterListComponent implements OnInit {
  bbCharacterList$ = this.charactersService.getCharacters();

  constructor(private charactersService: CharactersService) { }

  ngOnInit(): void {
    // this.charactersService.getCharacters().subscribe(console.log)
  }

}

<div @slideInRightList
  class="character-grid"
  *ngIf="bbCharacterList$ | async as characterList">
  <bc-character-card
    [character]="character"
    *ngFor="let character of characterList">
  </bc-character-card>
</div>
